schemas.formSchema:
{
  "type": "object",
  "properties": {
    "name": {
      "type": "string",
      "default": ""
    },
    "class": {
      "type": "string",
      "default": ""
    },
    "level": {
      "type": "number",
      "default": 1
    },
    "attributes": {
      "type": "object",
      "properties": {
        "Strength": {
          "type": "number",
          "default": 10
        },
        "Dexterity": {
          "type": "number",
          "default": 10
        },
        "Constitution": {
          "type": "number",
          "default": 10
        },\
        "Intelligence": {
          "type": "number",
          "default": 10
        },
        "Wisdom": {
          "type": "number",
          "default": 10
        },
        "Charisma": {
          "type": "number",
          "default": 10
        }
      }
    },
    "saves": {
      "type": "object",
      "properties": {
        "Strength Save": {
          "type": "number",
          "default": 0
        },
        "Dexterity Save": {
          "type": "number",
          "default": 0
        },
        "Constitution Save": {
          "type": "number",
          "default": 0
        },
        "Intelligence Save": {
          "type": "number",
          "default": 0
        },
        "Wisdom Save": {
          "type": "number",
          "default": 0
        },\
        "Charisma Save": {
          "type": "number",
          "default": 0
        }
      }
    },
    "skills": {
      "type": "array",
      "default": [],
      "items": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string"
          },
          "value": {
            "type": "number",
            "default": 0
          }
        }
      }
    },\
    "feats": {
      "type": "array",
      "default": [],
      "items": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "default": ""
          },
          "effect": {
            "type": "string",
            "default": ""
          }
        }
      }
    },\
    "backstory": {
      "type": "string",
      "widget": "textarea",
      "default": ""
    }
  },
  "required": [
    "name",
    "class",
    "level"
  ]
}

